// 1. Get the raw text from the 'output' property seen in the screenshot
const rawText = $input.first().json.output;

// 2. Clean up Markdown code blocks if they exist
// We use a case-insensitive regex to catch ```JSON or ```json
const cleanJson = rawText.replace(/```json/gi, '').replace(/```/g, '').trim();

// 3. Parse the string into actual data items
try {
  const parsedData = JSON.parse(cleanJson);

  // Return as an array of items for n8n to process properly
  return Array.isArray(parsedData) ? parsedData : [parsedData];

} catch (error) {
  // If parsing fails, return a standardized error object
  return [{
    outsourceability_score: 0,
    is_valid_lead: false,
    reason: "Error parsing JSON string",
    error_details: error.message,
    raw_received: rawText.substring(0, 100) // Helpful for debugging
  }];
}

const companyName = $("Loop Companies").first().json.company_name;
const results = $input.all()[0].json.organic || [];

// Regex to match required roles
const rolePattern = /(recruiter|talent|people|hr|founder|ceo|hiring)/i;

// Filter and format
const profiles = results.filter(item => {
  // 1. Must be a profile URL
  const isProfile = item.link && item.link.includes("linkedin.com/in/");
  // 2. Must match role keywords in title or snippet
  const textToCheck = (item.title + " " + item.snippet).toLowerCase();
  const hasRole = rolePattern.test(textToCheck);

  return isProfile && hasRole;
}).map(item => {
  return {
    url: item.link,
    company_name: companyName
  };
});

// Deduplicate by URL
const uniqueProfiles = [...new Map(profiles.map(item => [item['url'], item])).values()];

return uniqueProfiles;

// 1. Get Data
const jobs = $('Read Jobs Sheet').all();
const dms = $('Read DM Sheet').all();

// 2. Create a "Dictionary" of DMs by Company
const dmMap = {};

dms.forEach(item => {
  const company = (item.json.companyName || "").toLowerCase().trim();
  if (company) {
    if (!dmMap[company]) {
      dmMap[company] = [];
    }
    dmMap[company].push({
      fullName: item.json.fullName,
      title: item.json.jobTitle,
      headline: item.json.headline,
      url: item.json.linkedinPublicUrl
    });
  }
});

// 3. Attach DMs to Jobs (KEEP ALL JOBS)
const results = [];

jobs.forEach((jobItem, index) => {
  const job = jobItem.json;
  const companyName = (job.companyName || "").toLowerCase().trim();

  // Find matches (or empty array if none)
  const potentialDMs = dmMap[companyName] || [];

  // PUSH EVERYTHING (Even if 0 matches)
  results.push({
    json: {
      ...job,
      _jobRowIndex: index + 2,
      _match_count: potentialDMs.length, // Helpful for debugging
      _potential_candidates: potentialDMs
    }
  });
});

return results;

