// 1. Get the original data (which contains 'link', 'companyName', etc.)
// We refer to the node exactly as it appears in your screenshot: "Link Jobs to DMs"
const originalJobs = $('Link Jobs to DMs').all();

return items.map((item, index) => {
  try {
    // 2. Grab the specific original job that matches this AI result
    // We use [index] because the items follow the exact same order (1st Job -> 1st AI Answer)
    const originalJobData = originalJobs[index] ? originalJobs[index].json : {};

    // 3. Parse the AI Text
    // We check 'output' because that is what your screenshot shows
    let rawText = item.json.output || "{}";

    // Clean up if the AI added markdown blocks like ```json
    rawText = rawText.replace(/```json/g, "").replace(/```/g, "").trim();

    // Parse the text into a real object
    const parsedData = JSON.parse(rawText);
    const dm = parsedData.selected_dm || {};

    // 4. Return the MERGED data
    // We spread (...originalJobData) first, so we get 'link' back!
    return {
      json: {
        ...originalJobData, 
        decision_maker_name: dm.name || "No Match Found",
        decision_maker_title: dm.title || "",
        decision_maker_url: dm.linkedin_url || "",
        decision_maker_reason: dm.match_reason || ""
      }
    };

  } catch (error) {
    // Fallback in case of error, still return original data so the flow doesn't break
    const originalJobData = originalJobs[index] ? originalJobs[index].json : {};
    return {
      json: {
        ...originalJobData,
        decision_maker_name: "Error Parsing AI",
        decision_maker_reason: error.message
      }
    };
  }
});

// 1. Create a Set to remember links we have already seen
const seenLinks = new Set();

return items.map((item, index) => {
  // 2. Get the link from the current row
  // We use safe navigation and trim whitespace
  const link = (item.json.link || "").toString().trim();

  let isDuplicate = "No";

  // 3. Logic: If the Set already has this link, it's a duplicate.
  if (link && seenLinks.has(link)) {
    isDuplicate = "Yes";
  } else if (link) {
    // If it's new (and not empty), add it to the Set
    seenLinks.add(link);
    isDuplicate = "No";
  }

  // 4. Return the data with the new status and the exact Row Number
  // (Google Sheets starts at Row 1 (Header), so data starts at Row 2)
  return {
    json: {
      ...item.json,
      calculated_duplicate_status: isDuplicate,
      row_number_to_update: index + 2
    }
  };
});
